mapper.select:
select a.id,a.name,u.name as owner,a.startDate,a.endDate from tbl_activity a join tbl_user u on a.owner=u.id
u.name like '%' #{activity.owner} '%'
and a.name like '%' #{activity.name} '%'
and a.startDate > #{activity.startDate}
and a.endDate < #{activity.endDate}
order by a.createTime desc limit #{skipSize},#{pageSize}

select count(*) from tbl_activity a join tbl_user u on a.owner=u.id
u.name like '%' #{activity.owner} '%'
and a.name like '%' #{activity.name} '%'
and a.startDate > #{activity.startDate}
and a.endDate < #{activity.endDate}

mapper.delete:
delete from tbl_activity
where id in

    #{id}
